mapper.select:
select

from o_org


    and org_usage = #{orgUsage,jdbcType=INTEGER}


    and org_code = #{orgCode,jdbcType=VARCHAR}


    and org_name = #{orgName,jdbcType=VARCHAR}


    and parent_code = #{parentCode,jdbcType=VARCHAR}


    and num = #{num,jdbcType=INTEGER}


     and create_time >= #{startTime,jdbcType=TIMESTAMP} 


     and create_time <= #{endTime,jdbcType=TIMESTAMP} 


order by create_time desc

select
distinct o.id, o.org_usage, o.org_code, o.org_name, o.parent_code, o.num, o.create_time
from o_org o left join o_role_org r on o.org_code = r.org_code and o.org_usage = r.role_org_usage

    r.role_org_usage = #{roleOrgUsage,jdbcType=INTEGER}

    and r.role_code = #{roleCode,jdbcType=VARCHAR}



      and r.role_code in

        #{_item,jdbcType=VARCHAR}




order by create_time desc

select distinct o.id, o.org_usage, o.org_code, o.org_name, o.parent_code, o.num, o.create_time from o_org o left join o_admin_org a on o.org_code = a.org_code and o.org_usage = 0
and a.admin_id = #{adminId,jdbcType=BIGINT}
and a.admin_id in
#{_item,jdbcType=BIGINT}
order by o.create_time desc

select distinct o.id, o.org_usage, o.org_code, o.org_name, o.parent_code, o.num, o.create_time from o_org o left join o_user_org uo on o.org_code = uo.org_code and o.org_usage = 1
and uo.user_id = #{userId,jdbcType=BIGINT}
and uo.user_id in
#{_item,jdbcType=BIGINT}
order by o.create_time desc
